SELECT DISTINCT o.mailNo, o.id, o.status, o.line_type, o.weight, o.accept_time, o.create_time, CONCAT_WS("，", t1.prov, t1.city, t1.district, t1.address) as fromAddr, CONCAT_WS("，", t2.prov, t2.city, t2.district, t2.address) as toAddr, t2.numProv as numProv FROM ec_core_order o, ec_core_traderinfo t1, ec_core_traderinfo t2 WHERE o.customer_id IN
#{customerId}
AND o.id = t1.order_id AND t1.trade_type='0' AND o.id = t2.order_id AND t2.trade_type='1'
AND o.partitiondate BETWEEN #{starttime} AND #{endtime}
AND t2.prov = #{prov} AND t2.city = #{city} AND t2.district = #{district} GROUP BY o.mailNo ORDER BY o.partitiondate DESC, o.id DESC
LIMIT #{startIndex}, #{pageNum}

SELECT DISTINCT o.mailNo, o.id, o.status, o.line_type, o.weight, o.accept_time, o.create_time, o.freight_type, o.trim_freight, o.order_type, o.tx_logistic_id, o.customer_id, CONCAT_WS("，", t1.prov, t1.city, t1.district, t1.address) as #{addr}, t1.numProv as numProv FROM ec_core_order o, ec_core_traderinfo t1 WHERE o.customer_id IN
#{customerId}
AND o.id = t1.order_id AND t1.trade_type=#{tradeType}
AND o.create_time > #{ucUpdateTime}
AND o.partitiondate BETWEEN CONCAT(#{starttime}, ' 00:00:00') AND CONCAT(#{endtime}, ' 23:59:59')
AND t1.prov = #{prov} AND t1.city = #{city} AND t1.district = #{district} GROUP BY o.mailNo ORDER BY o.partitiondate DESC, o.id DESC
LIMIT #{startIndex}, #{pageNum}

SELECT DISTINCT o.mailNo, o.id, o.status, o.line_type, o.weight, o.accept_time, o.create_time, CONCAT_WS("，", t1.prov, t1.city, t1.district, t1.address) as fromAddr, CONCAT_WS("，", t2.prov, t2.city, t2.district, t2.address) as toAddr, t2.numProv as numProv FROM ec_core_order o, ec_core_traderinfo t1, ec_core_traderinfo t2 WHERE o.customer_id IN
