mapper.select:

		select send_start_time as sendStartTime,(accept/n) as ratio,accept,n as number from (
		select send_start_time,sum(mystatus) as accept,count(1) as n from (
		select send_start_time, 
  			   case
  			   when (status ='ACCEPT' OR status ='GOT') then 1
  			   else 0
  			   end as mystatus
 		from ec_core_order






						and 
						 exists ( 
		    			 select 1 from ec_core_user where customer_id = taobao_encode_key
		                 and parentid = #{theId}
		                )







 					and customer_id = #{theId}




			and date(send_start_time) >= #{startDate}



			and date(send_start_time) <= #{endDate}



		)t_1
 		group by send_start_time
		)t_2


			select count(1) from ec_core_order



			and date(send_start_time) = #{startDate}



			and date(send_end_time) = #{endDate}



			and (status ='ACCEPT' OR status ='GOT')


 			and customer_id = #{theId}


			 select count(1) from (
			 select taobao_encode_key from ec_core_user where  
			 taobao_encode_key is not null


 			and parentid = #{theId}

 		)t_1 left join ec_core_order t2 on t_1.taobao_encode_key = t2.customer_id  )t_1 left join ec_core_order t2 on t_1.taobao_encode_key = t2.customer_id

			and date(send_start_time) = #{startDate}



			and date(send_end_time) = #{endDate}



			and (status ='ACCEPT' OR status ='GOT')


		select hourstype,sum(1) as number from (
		select 
		 case  
		 when hours <= 24 then 1
		 when hours <= 48 then 2
		 when hours <= 72 then 3 
		 else 0
		 end hourstype,hours
		 from (
		select TIMESTAMPDIFF(HOUR,send_start_time,accept_time) as hours,accept_time,send_start_time
		from ec_core_order



			and date(send_start_time) >= #{startDate}



			and date(send_start_time) <= #{endDate}

			and (status ='ACCEPT' OR status ='GOT')

 			and customer_id = #{theId}



		)t_1
		)t_2 group by hourstype


		select hourstype,sum(1) as number from (
			select 
			 case  
			 when hours <= 24 then 1
			 when hours <= 48 then 2
			 when hours <= 72 then 3 
			 else 0
			 end hourstype,hours
			 from (
			select TIMESTAMPDIFF(HOUR,send_start_time,accept_time) as hours,accept_time,send_start_time from (
			select taobao_encode_key from ec_core_user where 
			taobao_encode_key is not null


 			and parentid = #{theId}

		)t_1 left join ec_core_order t2 on t_1.taobao_encode_key = t2.customer_id 


			and date(send_start_time) >= #{startDate}



			and date(send_start_time) <= #{endDate}


			and (status ='ACCEPT' OR status ='GOT') 

		)t_2
		)t_3 group by hourstype
